package process_test

import (
	"math/big"
	"testing"

	"github.com/ethereum/go-ethereum/common"
	"github.com/freeverseio/crypto-soccer/go/contracts/autogenerated/assets"
	"github.com/freeverseio/crypto-soccer/go/synchronizer/process"
	"github.com/freeverseio/crypto-soccer/go/testutils"
)

func TestAssetsInitProcess(t *testing.T) {
	err := universedb.Begin()
	if err != nil {
		t.Fatal(err)
	}
	defer universedb.Rollback()
	err = relaydb.Begin()
	if err != nil {
		t.Fatal(err)
	}
	defer relaydb.Rollback()
	bc, err := testutils.NewBlockchainNodeDeployAndInit()
	if err != nil {
		t.Fatal(err)
	}
	process, err := process.NewAssetsInitProcessor(bc.Contracts, universedb, relaydb)
	if err != nil {
		t.Fatal(err)
	}

	creator := common.HexToAddress("123")

	event := assets.AssetsAssetsInit{
		CreatorAddr: creator,
	}
	err = process.Process(event)
	if err != nil {
		t.Fatal(err)
	}
	team, err := universedb.GetTeam(big.NewInt(1))
	if err != nil {
		t.Fatal(err)
	}
	if team.State.Name != "Academy" {
		t.Fatalf("Wrong team name. Expected: Academy, result: %v", team)
	}
	if team.State.Owner != creator.String() {
		t.Fatalf("Wrong team owner: %v", team.State.Owner)
	}
}
