package process_test

import (
	"math/big"
	"testing"

	"github.com/freeverseio/crypto-soccer/go/contracts/autogenerated/assets"
	"github.com/freeverseio/crypto-soccer/go/names"
	"github.com/freeverseio/crypto-soccer/go/synchronizer/process"
	"github.com/freeverseio/crypto-soccer/go/synchronizer/storage"
	"github.com/freeverseio/crypto-soccer/go/testutils"
)

func TestDivisionCreationProcess(t *testing.T) {
	tx, err := universedb.Begin()
	if err != nil {
		t.Fatal(err)
	}
	defer tx.Rollback()
	relaytx, err := relaydb.Begin()
	if err != nil {
		t.Fatal(err)
	}
	defer relaytx.Rollback()

	namesdb, err := names.New("../../names/sql/names.db")
	if err != nil {
		t.Fatal(err)
	}
	bc, err := testutils.NewBlockchainNodeDeployAndInit()
	if err != nil {
		t.Fatal(err)
	}
	process, err := process.NewDivisionCreationProcessor(bc.Contracts, namesdb)
	if err != nil {
		t.Fatal(err)
	}

	event := assets.AssetsDivisionCreation{
		Timezone:             uint8(1),
		CountryIdxInTZ:       big.NewInt(0),
		DivisionIdxInCountry: big.NewInt(0),
	}
	err = process.Process(tx, relaytx, event)
	if err != nil {
		t.Fatal(err)
	}
	player, err := storage.PlayerByPlayerId(tx, big.NewInt(274877906944))
	if err != nil {
		t.Fatal(err)
	}
	if player.State.Name == "" {
		t.Fatal("name is empty")
	}
	if player.DayOfBirth == 0 {
		t.Fatal("dayOfBirth is 0")
	}
}
